<?php class PessoaJuridica extends Pessoa {    protected $cnpj;    protected $ccm;    protected $inscEstadual;    public function getValores() {        $valores["idPessoa"]=$this->id;        $valores["cnpj"]=$this->cnpj;        $valores["inscEstadual"]=$this->inscEstadual;        $valores["ccm"]=$this->ccm;        return $valores;    }        /**     * valida��o do cadastro de pessoaJuridica     *     * @param unknown_type $acao     */    public function valida($acao) {        try {            parent::valida($acao);            $errors = new FormException();        }catch(FormException $errors ){ }            if($this->cnpj=="") $errors->addError("O cnpj é obrigatório");            if(sizeof($errors->getErrors())>0)            throw $errors;    }}?>
