<?phpclass PDOSingletonEAD {    private static $carregadaEAD;    private static $conEAD;    private function __construct() {        self::$carreadaEAD = false;    }    public function getInstance($urlCon=null, $user=null, $pass=null) {        if (FALSE == self::$carregadaEAD) {            if (NULL == self::$conEAD)                self::$conEAD = new PDO($urlCon, $user, $pass);            self::$carregadaEAD = TRUE;            return self::$conEAD;        }        return self::$conEAD;    }}?>