<?class PDOSingletonSISTESTE {    private static $carregadaSISTESTE;    private static $conSISTESTE;    private function __construct() {        self::$carreadaSISTESTE = false;    }    public function getInstance($urlCon=null, $user=null, $pass=null) {        if (FALSE == self::$carregadaSISTESTE) {            if (NULL == self::$conSISTESTE)                self::$conSISTESTE = new PDO($urlCon, $user, $pass);            self::$carregadaSISTESTE = TRUE;            return self::$conSISTESTE;        }        return self::$conSISTESTE;    }}?>